import React, { Component } from 'react'
import { TitleHeader, TextInput, Button } from './styles'

class AuthForm extends Component {
  constructor (props) {
    super(props)
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
    this.state = {}
  }

  handleChange (event) {
    const st = this.state
    st[event.target.name] = event.target.value
    this.setState({ formData: st })
  }

  render () {
    const { labels } = this.props
    const { title } = this.props.title
    const { handleConfirmAction } = this.props.handleConfirmAction

    const lbs = labels.map((lb) => {
      return (
        <>
          <label>
            {lb.title}
            <TextInput
              type={lb.type}
              name={lb.name}
              value={lb.value}
              onChange={this.handleChange}
            />
          </label>
          <br />
        </>
      )
    }
    )

    return (
      <>
        <TitleHeader>{title}</TitleHeader>
        {lbs}
        <Button onClick={() => handleConfirmAction(this.state)}>{title}</Button>
      </>
    )
  }
}

export default AuthForm
