import React, { Component } from 'react'

import * as api from '../../../api/api'
import { loginUrl } from '../../../api/routes'

import { Container, LoginHeader, TextInput, Button } from './styles'

class LoginUser extends Component {
  constructor (props) {
    super(props)
    this.handleLogin = this.handleLogin.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.state = {
      credentials: { username: '', password: '', email: '' }
    }
  }

  async handleLogin (event) {
    const { onAppAuthChangeInfo } = this.props
    const user = await api.post(loginUrl(), this.state.credentials)
    if (user.key !== undefined) {
      localStorage.setItem('token', user.key)
      localStorage.setItem('username', this.state.credentials.username)
      if (onAppAuthChangeInfo !== undefined) {
        onAppAuthChangeInfo()
      }
    } else {
      localStorage.clear()
    }
    this.setState({
      credentials: { username: '', password: '', email: '' }
    })
  }

  handleChange (event) {
    const cr = this.state.credentials
    cr[event.target.name] = event.target.value
    this.setState({ credentials: cr })
  }

  render () {
    return (
      <Container>
        <LoginHeader>Login</LoginHeader>
        <label>
          Username
          <TextInput
            type='text'
            name='username'
            value={this.state.credentials.username}
            onChange={this.handleChange}
          />
        </label>
        <br />
        <label>
          Email
          <TextInput
            type='email'
            name='email'
            value={this.state.credentials.email}
            onChange={this.handleChange}
          />
        </label>
        <br />
        <label>
          Password
          <TextInput
            type='password'
            name='password'
            value={this.state.credentials.password}
            onChange={this.handleChange}
          />
        </label>
        <Button onClick={this.handleLogin}>Login</Button>
      </Container>
    )
  }
}

export default LoginUser
