import React, { Component } from 'react'
import { TitleHeader, TextInput, Button } from './styles'

class AuthForm extends Component {
  constructor (props) {
    super(props)
    this.handleChange = this.handleChange.bind(this)
    this.state = {
      credentials: { username: '', password: '', email: '' }
    }
  }

  handleChange (event) {
    const cr = this.state.credentials
    cr[event.target.name] = event.target.value
    this.setState({ credentials: cr })
  }

  render () {
    const { labels } = this.props
    const { title } = props.title
    const { handleConfirmAction } = props.handleConfirmAction

    const lbs = labels.map((lb) => {
      return (
        <>
          <label>
            {lb.title}
            <TextInput
              type={lb.type}
              name={lb.name}
              value={lb.value}
              onChange={this.handleChange}
            />
          </label>
          <br />
        </>
      )
    }
    )

    return (
      <>
        <TitleHeader>{title}</TitleHeader>
        {lbs}
        <Button onClick={props.handleConfirmAction}>{title}</Button>
      </>
    )
  }
}

export default AuthForm

