import React, { Component } from 'react'

import * as api from '../../../api/api'
import { loginUrl } from '../../../api/routes'

import { Container, TitleHeader, TextInput, Button } from './styles'

const AuthForm = (props) => {

  const labels = props.labels.map((lb) => {
    return (
      <>
        <label>
          {lb.title}
          <TextInput
            type={lb.type}
            name={lb.name}
            value={lb.value}
            onChange={lb.handleChangeClb}
          />
        </label>
        <br />
      </>
    )
  }
  )

  return (
    <>
      <TitleHeader>{props.title}</TitleHeader>
      {labels}
      <Button onClick={props.handleConfirmAction}>{props.title}</Button>
    </>
  )
}

class LoginUser extends Component {
  constructor (props) {
    super(props)
    this.onHandleLogin = this.onHandleLogin.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.state = {
      credentials: { username: '', password: '', email: '' }
    }
  }

  async onHandleLogin (event) {
    const { onAppAuthChangeInfo } = this.props
    const res = await api.post(loginUrl(), this.state.credentials)

    if (res.token !== undefined) {
      console.log('authenticated')
    } else if (res.non_field_errors !== undefined) {
      console.log(res.token)
    }

    // console.log(auth.token)
    // console.log(auth.user.username)

    // if (user.key !== undefined) {
      // localStorage.setItem('token', user.key)
      // localStorage.setItem('username', this.state.credentials.username)
      // if (onAppAuthChangeInfo !== undefined) {
        // onAppAuthChangeInfo()
      // }
    // } else {
      // localStorage.clear()
    // }
    this.setState({
      credentials: { username: '', password: '', email: '' }
    })
  }

  handleChange (event) {
    const cr = this.state.credentials
    cr[event.target.name] = event.target.value
    this.setState({ credentials: cr })
  }

  render () {
    const lbs = [
      {
        title: 'Username',
        type: 'text',
        name: 'username',
        value: this.state.credentials.username,
        handleChangeClb: this.handleChange
      },
      {
        title: 'Email',
        type: 'email',
        name: 'email',
        value: this.state.credentials.email,
        handleChangeClb: this.handleChange
      },
      {
        title: 'Password',
        type: 'password',
        name: 'password',
        value: this.state.credentials.password,
        handleChangeClb: this.handleChange
      }
    ]

    return (
      <Container>
        <AuthForm title='Login' labels={lbs} handleConfirmAction={this.onHandleLogin} />
      </Container>
    )
  }
}

export default LoginUser
