import React, { Component } from 'react'
import { connect } from 'react-redux'

import * as api from '../../../../api/api'
import { loginUrl } from '../../../../api/routes'

import { Container, TitleHeader } from '../styles'
import LoginForm from './LoginForm'

class LoginUser extends Component {
  constructor (props) {
    super(props)
  }

  async handleLogin (event) {
    const { onAppAuthChangeInfo } = this.props
    const user = await api.post(loginUrl(), this.state.credentials)
    if (user.key !== undefined) {
      localStorage.setItem('token', user.key)
      localStorage.setItem('username', this.state.credentials.username)
      if (onAppAuthChangeInfo !== undefined) {
        onAppAuthChangeInfo()
      }
    } else {
      localStorage.clear()
    }
    this.setState({
      credentials: { username: '', password: '', email: '' }
    })
  }

  getFormData = (credentials) => {
    console.log(credentials)
  }

  render () {
    return (
      <Container>
        <TitleHeader>Login</TitleHeader>
        <LoginForm getFormData={this.getFormData}/>
      </Container>
    )
  }
}

const mapStateToProps = state => {
  console.log('from mapStateToProps')
  console.log(state)
  const { credentials } = state
  return { credit: credentials }
}

export default connect(mapStateToProps, {})(LoginUser)
