import React, { Component } from 'react'

import { connect } from 'react-redux'
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom'

import NavBar from '../NavBar/NavBar'
import history from '../history'

import ProtectedRoutes from './ProtectedRoutes'
import UnauthRedirect from './UnauthRedirect'

import LoginUser from '../../auth/components/LoginUser'
// import LogoutUser from '../../auth/components/LogoutUser'
import RegUser from '../../auth/components/RegUser'

// import Dashboard from '../../main/Dashboard'
import NotFound from '../../main/NotFound'

// import Gold from '../../resources/components/Gold'
// import Silver from '../../resources/components/Silver'
// import Cash from '../../resources/components/Cash'

const PrivateRoutes = ({ component: Component, auth }) => (
  <Route render={props => auth === true
    ? <Component auth={auth} {...props} />
    : <Redirect to={{ pathname: '/redirect' }} />}
  />
)

class Routes extends Component {
  render () {
    return (
      <>
        <Router history={history}>
          <NavBar />
          <Switch>
            <PrivateRoutes path='/auth1' auth={this.props.isAuth} component={ProtectedRoutes} />
            <PrivateRoutes path='/auth2' auth={this.props.isAuth} component={ProtectedRoutes} />
            <PrivateRoutes path='/auth3' auth={this.props.isAuth} component={ProtectedRoutes} />
            <Route path='/redirect' component={UnauthRedirect} />
            {/* <Route path='/login'><LoginUser /></Route> */}
            {/* <Route path='/register'><RegUser /></Route> */}
            {/* <Route component={NotFound} /> */}
          </Switch>
        </Router>
      </>
    )
  }
}

function mapStateToProps (state) {
  const { authentication } = state
  return { isAuth: authentication.isAuthenticated }
}

export default connect(mapStateToProps, null)(Routes)
