import React, { Component } from 'react'
import { TextInput, Button } from '../styles'

class LoginForm extends Component {
  constructor (props) {
    super(props)
    this.state = {
      credentials: { username: '', password: '', email: '' }
    }
  }

  handleChange = (event) => {
    const cr = this.state.credentials
    cr[event.target.name] = event.target.value
    this.setState({ credentials: cr })
  }

  handleConfirm = (event) => {
    const { getFormData } = this.props

    if (getFormData !== undefined) {
        getFormData(this.state.credentials)
    }
  }

  render () {
    return (
      <form>
        <label>
          Username
          <TextInput
            type='text'
            name='username'
            value={this.state.credentials.username}
            onChange={this.handleChange}
          />
        </label>
        <br />
        <label>
          Email
          <TextInput
            type='email'
            name='email'
            value={this.state.credentials.email}
            onChange={this.handleChange}
          />
        </label>
        <br />
        <label>
          Password
          <TextInput
            type='password'
            name='password'
            value={this.state.credentials.password}
            onChange={this.handleChange}
          />
        </label>
        <Button onClick={this.handleConfirm}>Login</Button>
      </form>
    )
  }
}

export default LoginForm
