import React, { Component } from 'react'

import * as api from '../../../api/api'
import { loginUrl } from '../../../api/routes'

import { Container, TitleHeader, TextInput, Button } from './styles'

const AuthForm = (props) => {

  const labels = props.labels.map((lb) => {
    return (
      <label>
        {lb.title}
        <TextInput
          type={lb.type}
          name={lb.name}
          value={lb.value}
          onChange={lb.onChangeClb}
        />
      </label>
    )
    }
  )

  return (
    <>
      <TitleHeader>{props.title}</TitleHeader>
      {labels}
    </>
  )
}

class LoginUser extends Component {
  constructor (props) {
    super(props)
    this.handleLogin = this.handleLogin.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.state = {
      credentials: { username: '', password: '', email: '' }
    }
  }

  async handleLogin (event) {
    const { onAppAuthChangeInfo } = this.props
    const auth = await api.post(loginUrl(), this.state.credentials)
    console.log(auth)
    console.log(auth.token)
    console.log(auth.user.username)
    // if (user.key !== undefined) {
      // localStorage.setItem('token', user.key)
      // localStorage.setItem('username', this.state.credentials.username)
      // if (onAppAuthChangeInfo !== undefined) {
        // onAppAuthChangeInfo()
      // }
    // } else {
      // localStorage.clear()
    // }
    this.setState({
      credentials: { username: '', password: '', email: '' }
    })
  }

  handleChange (event) {
    const cr = this.state.credentials
    cr[event.target.name] = event.target.value
    this.setState({ credentials: cr })
  }

  render () {
    const label = {
      title: 'ihaaaa',
      type: 'text',
      name: 'dupa',
      value: this.state.credentials.username
    }

    return (
      <Container>
        <AuthForm title='Login' onChangeClb={this.handleChange} label={label} />
        <label>
          Username
          <TextInput
            type='text'
            name='username'
            value={this.state.credentials.username}
            onChange={this.handleChange}
          />
        </label>
        <br />
        <label>
          Email
          <TextInput
            type='email'
            name='email'
            value={this.state.credentials.email}
            onChange={this.handleChange}
          />
        </label>
        <br />
        <label>
          Password
          <TextInput
            type='password'
            name='password'
            value={this.state.credentials.password}
            onChange={this.handleChange}
          />
        </label>
        <Button onClick={this.handleLogin}>Login</Button>
      </Container>
    )
  }
}

export default LoginUser
